    return (
        <><>
            <table className="table-title ">
                <thead>
                    <tr>
                        <th>NÂ°</th>
                        <th>Player</th>
                        <th>MP</th>
                        <th>FW</th>
                        <th>FL</th>
                        <th >FD</th>
                    </tr>
                </thead>
                <tbody>
                    {players.sort((a, b) => a.fd > b.fd ? 1 : -1)
                    .map((p, i) => (
                        <tr >

                            <td> {i + 1}</td>
                            <td>{p.player}</td>


                            <td>{p.mp}</td>
                            <td>{p.fw}</td>
                            <td>{p.fl}</td>
                            <td >{p.fw - p.fl}</td>
                        </tr>
                    ))}



                </tbody>
            </table>
        </></>
  )
}


