import React from 'react'
import { useEffect,useState } from 'react';
import "assets/css/groups.css";
import Axios from 'axios';
const Groups = (props) => {
    const [matchinfo, setmatchinfo] = useState([]);
    useEffect(() => {
        Axios.get("http://absc-snooker.herokuapp.com/ReadTeamsMatches/").then((response) => {
            setmatchinfo([...response.data]);
        })
    }, []);

      const [playerlist, setplayerlist] = useState([]);

      useEffect(() => {
          Axios.get("http://absc-snooker.herokuapp.com/ReadTeamsList/").then((response) => {
              setplayerlist([...response.data]);
          })
      }, []);
      const players = playerlist.filter(p => p.group == props.number )
    return (
        <>
        <table className="table-title">
        <thead>   
          <tr>
            <th>NÂ°</th>
            <th>Player</th>
            <th>MP</th>
            <th>FW</th>
            <th>FL</th>
            <th>FD</th>
          </tr>
        </thead>
        <tbody>  
          <tr>
          <td>1</td>
          {players.map((p)=>(
             <td>{p.player}</td>
            ))}

            <td>2</td>
            <td>14</td>
            <td>4</td>
            <td>10</td>
          </tr>




        </tbody>
    </table>
    </>
  )
}


export default Groups
